// src/components/AccountBalance.js

import React, {Component} from 'react';

class AccountBalance extends Component {

  reRender = () => {
    // calling the forceUpdate() method
    this.forceUpdate();
  };
  render() {
    return (

      <div>
        Balance: {this.props.accountBalance}
      </div>

    );
  }
}

export default AccountBalance;
